package project_pgm;

public class ArryaSum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int array[] = {10,20,30,40,50};
		int sum=0;
		for(int i=0;i<array.length;i++)
		{
			sum+=array[i];
		}
		System.out.println("Summ of Array Elements = "+sum);


	}

}
